mapper.insert:
INSERT
  INTO MEMBER 
(
  MEMBER_NO
, MEMBER_NAME  
, MEMBER_ID
, MEMBER_PWD
, PHONE
, EMAIL
, POSTCODE
, BS_ADDRESS
, DT_ADDRESS
, CUSTOM
, MEMBER_STATUS
)
VALUES
(
  SEQ_MEMBER_NO.NEXTVAL
, #{ memberName }  
, #{ memberId }
, #{ memberPwd }
, #{ phone }
, #{ email }
, #{ postCode }
, #{ bsAddress }
, #{ dtAddress }
, '구매자'
, 'Y'
)

