, #{ businessName }
, #{ businessNo }  
, #{ memberId }
, #{ memberPwd }
, #{ phone }
, #{ email }
, #{ postCode }
, #{ bsAddress }
, #{ dtAddress }
, '판매자'
, 'N'
)

mapper.update:
UPDATE 
      TBL_MEMBER 
  SET NICKNAME = #{ nickname }
    , PHONE = #{ phone }
    , EMAIL = #{ email }
    , ADDRESS = #{ address }
WHERE MEMBER_STATUS = 'Y'
  AND MEMBER_NO = #{ memberNo }

mapper.select:
SELECT 
		MEMBER_ID
  FROM  MEMBER
 WHERE  MEMBER_NAME = #{ memberName }
   AND  PHONE = #{ phone }
