mapper.select:
SELECT A.BANNER_NO, A.BANNER_GEN_DATE, A.BANNER_DEL_DATE, A.BANNER_POST_DATE, A.BANNER_END_DATE FROM (SELECT ROWNUM RNUM, B.BANNER_NO, B.BANNER_GEN_DATE, B.BANNER_DEL_DATE, B.BANNER_POST_DATE, B.BANNER_END_DATE FROM (SELECT C.BANNER_NO, C.BANNER_GEN_DATE, C.BANNER_DEL_DATE, C.BANNER_POST_DATE, C.BANNER_END_DATE FROM BANNER C ORDER BY C.BANNER_POST_DATE ASC ) B
WHERE ROWNUM <= #{ endRow }
)A WHERE A.RNUM >= #{ startRow } ORDER BY 1 ASC
