mapper.insert:
INSERT 
 INTO TBL_ATTACHMENT
     (
   FILE_NO
 , COMMUNITY_NO
 , FILE_ORIGINAL_NAME
 , FILE_SAVED_NAME
 , FILE_PATH
 , FILE_SIZE
 , THUMBNAILPATH
 , CUSTOM
 )
 VALUES
 (
 	SEQ_FILE_NO.NEXTVAL
     , SEQ_COMMUNITY_NO.CURRVAL
     , #{ originalName }
     , #{ savedName }
     , #{ savePath }
     , #{ fileType }
     , #{ thumbnailPath, jdbcType=VARCHAR }
     , 1 
 )

mapper.select:
SELECT A.COMMUNITY_NO , D.FILE_SIZE , D.THUMBNAILPATH , A.COMMUNITY_TITLE , A.COMMUNITY_STORY , A.MEMBER_NO , A.COMMUNITY_WRITE_DATE , A.CUSTOM FROM (SELECT ROWNUM RNUM , B.COMMUNITY_NO , B.COMMUNITY_TITLE , B.COMMUNITY_STORY , B.MEMBER_NO , B.COMMUNITY_WRITE_DATE , B.CUSTOM FROM (SELECT C.COMMUNITY_NO , C.COMMUNITY_TITLE , C.COMMUNITY_STORY , C.MEMBER_NO , C.COMMUNITY_WRITE_DATE , C.CUSTOM FROM COMMUNITY C WHERE C.CUSTOM = 1 ORDER BY C.COMMUNITY_NO DESC ) B
WHERE ROWNUM <= #{ endRow }
) A JOIN TBL_ATTACHMENT D ON (A.COMMUNITY_NO = D.COMMUNITY_NO) JOIN MEMBER E ON(A.MEMBER_NO = E.MEMBER_NO) WHERE A.RNUM >= #{ startRow } ORDER BY 1 DESC
